Scripts.OnLeave:
if not self:GetParent().Track.Selected then
	self:GetParent():UnlockHighlight();
end

Scripts.OnShow:
UIDropDownMenu_Initialize(self, DailyAzeroth_InitCharacterDropdown);

self.selectedName = "Select character...";
self.selectedValue = 0;

UIDropDownMenu_SetText(self, self.selectedName);

UIDropDownMenu_Initialize(self, DailyAzeroth_InitIconDropdown);

self.selectedName = "Icon Categories...";
self.selectedValue = 0;

UIDropDownMenu_SetText(self, self.selectedName);

self.selectedName = "Select a class...";
self.selectedValue = 0;

UIDropDownMenu_SetText(self, self.selectedName);
